mapper.insert:
insert into MEMBER (
	NAME, PWD, DEPT, CLASSES, GENDER
) values (
	#{name}, #{pwd}, #{dept}, #{classes}, #{gender}
)

mapper.select:
select NO
from MEMBER
where NAME = #{name}
and PWD = #{pwd}

select NO, NAME, DEPT, CLASSES, GENDER
from MEMBER
where NO = #{value}

mapper.update:
update MEMBER
NAME = #{name}, GENDER = #{gender}, CLASSES = #{classes}, DEPT = #{dept},
where NO = #{no}
