mapper.select:
select S.SEQ as id, S.SEQ, S.TITLE, S.CONTENT, S.TYPE, M.NAME from SAMPLE as S left join MEMBER as M on S.MEMBER_NO = M.NO where DELETE_AT = 'N'
and upper(M.NAME) like #{LIKE_TITLE,jdbcType=VARCHAR}
and upper(TITLE) like #{LIKE_TITLE,jdbcType=VARCHAR}
and TYPE = #{CALENDAR_SELECT}
order by SEQ desc;

mapper.update:
update SAMPLE
   set DELETE_AT = 'Y'
 where SEQ = #{SEQ,jdbcType=INTEGER}
